export const INVALID_ARRAY_PATHS_MESSAGE: string =
  'You have not provided any values for paths, so no values can be retrieved from state.';

export const INVALID_PATHS_MESSAGE: string = [
  'First parameter passed must be either an array or a plain object.',
  'If you are creating a standard selector, pass an array of either',
  'properties on the state to retrieve, or custom selector functions.',
  'If creating a structured selector, pass a plain object with source',
  'and destination properties, where source is an array of properties',
  'or custom selector functions, and destination is an array of property',
  'names to assign the values from source to.',
].join(' ');

export const INVALID_OBJECT_PATH_MESSAGE: string = `
When providing an object path, you must provide the following properties:
  * path: the path to retrieve, e.g. "foo.bar"
  * argIndx: the index of the argument to retrieve the path from
`.trim();

export const INVALID_PATH_MESSAGE: string = `
Path provided is of invalid type. It can be any one of the following values:
  * Dot-bracket notation, e.g. "foo.bar" or "bar[0].baz"
  * Number index, e.g. 0
  * Object {path, argIndex}, e.g. {path: "foo.bar", argIndex: 1}
  * Selector function
`.trim();
// external dependencies
import { deepEqual as isDeeplyEqual, sameValueZeroEqual } from 'fast-equals';
import { createIdentity } from 'identitate';
import { createSelectorCreator, defaultMemoize } from 'reselect';
import { get } from 'unchanged';

// constants
import { INVALID_OBJECT_PATH_MESSAGE, INVALID_PATH_MESSAGE } from './constants';

const hasOwnProperty = Object.prototype.hasOwnProperty;

/**
 * @private
 *
 * @function isFunctionPath
 *
 * @description
 * is the path a function
 *
 * @param path the path to test
 * @param type the typeof value for the path
 * @returns is the path a function
 */
export const isFunctionPath = (
  path: selectorator.Path,
  type: string,
): path is Function => type === 'function';

/**
 * @private
 *
 * @function isObjectPath
 *
 * @description
 * is the path an object
 *
 * @param path the path to test
 * @param type the typeof value for the path
 * @returns is the path an object
 */
export const isObjectPath = (
  path: selectorator.Path,
  type: string,
): path is selectorator.PathObject => !!path && type === 'object';

/**
 * @private
 *
 * @function isUnchangedPath
 *
 * @description
 * is the path an unchanged path value
 *
 * @param path the path to test
 * @param type the typeof value for the path
 * @returns is the path an unchanged path value
 */
export const isUnchangedPath = (
  path: selectorator.Path,
  type: string,
): path is string | number | (string | number)[] =>
  type === 'string' || type === 'number' || Array.isArray(path);

/**
 * @private
 *
 * @function createIdentitySelector
 *
 * @description
 * based on the path passed, create the identity function for it or return the function itself
 *
 * @param path nested path to retrieve from the state object
 * @returns identity function to retrieve value from state for given property
 */
export const createIdentitySelector = (path: selectorator.Path): Function => {
  const type: string = typeof path;

  if (isFunctionPath(path, type)) {
    return path;
  }

  if (isUnchangedPath(path, type)) {
    return (state: PlainObject): any => get(path, state);
  }

  if (isObjectPath(path, type)) {
    if (
      hasOwnProperty.call(path, 'path') &&
      hasOwnProperty.call(path, 'argIndex')
    ) {
      const selectorIdentity: Function = createIdentity(path.argIndex);

      return function () {
        return get(path.path, selectorIdentity.apply(null, arguments));
      };
    }

    throw new ReferenceError(INVALID_OBJECT_PATH_MESSAGE);
  }

  throw new TypeError(INVALID_PATH_MESSAGE);
};

/**
 * @private
 *
 * @function getSelectorCreator
 *
 * @description
 * get the creator function to use when generating the selector
 *
 * @param deepEqual should the memoizer be based on strict equality
 * @param isEqual the custom equality method to use when comparing values
 * @param memoizer custom selector memoizer
 * @param memoizerParams custom parameters to pass to the memoizer function
 * @returns function to create selector with
 */
export const getSelectorCreator = ({
  deepEqual = false,
  isEqual = sameValueZeroEqual,
  memoizer,
  memoizerParams = [],
}: selectorator.Options): Function => {
  const memoizerFn: Function = memoizer || defaultMemoize;
  const equals: Function = deepEqual ? isDeeplyEqual : isEqual;

  return (createSelectorCreator as any).call(  // fix strict mode error
    null,
    memoizerFn,
    equals,
    ...memoizerParams,
  );
};

/**
 * @private
 *
 * @function getStandardSelector
 *
 * @description
 * get a standard selector based on the paths and getComputedValue provided
 *
 * @param paths paths to retrieve values from state from
 * @param selectorCreator function to create selector with
 * @param getComputedValue function to compute values with, receiving properties in state based
 *   on paths and returning computed values from them (defaults to pass-through identity function)
 * @returns selector to return computed value from state
 */
export const getStandardSelector = (
  paths: selectorator.Path[],
  selectorCreator: Function,
  getComputedValue: Function,
): Function =>
  selectorCreator(paths.map(createIdentitySelector), getComputedValue);

/**
 * @private
 *
 * @function getStructuredObject
 *
 * @description
 * get the structured object based on the computed selector values
 *
 * @param properties properties to assign values from state to
 * @returns object of property => selected value pairs
 */
export const getStructuredObject = (properties: string[]): Function => (
  ...values: any[]
) =>
  properties.reduce(
    (
      structuredObject: PlainObject,
      property: string,
      index: number,
    ): PlainObject => {
      structuredObject[property] = values[index];

      return structuredObject;
    },
    {},
  );

/**
 * @private
 *
 * @function getStructuredSelector
 *
 * @description
 * get an object of property => selected value pairs bsaed on paths
 *
 * @param paths property => path pairs, where path is state value to retrieve and assign to property
 * @param selectorCreator function to create selector with
 * @returns selector to return structured values from state
 */
export const getStructuredSelector = (
  paths: PlainObject,
  selectorCreator: Function,
): Function => {
  const destinationKeys: string[] = Object.keys(paths);
  const selectors: Function[] = destinationKeys.map(
    (key) => createIdentitySelector(paths[key]),
  );

  return selectorCreator(selectors, getStructuredObject(destinationKeys));
};
// external dependencies
import { identity } from 'identitate';

// constants
import {
  INVALID_ARRAY_PATHS_MESSAGE,
  INVALID_PATHS_MESSAGE,
} from './constants';

// utils
import {
  getSelectorCreator,
  getStandardSelector,
  getStructuredSelector,
} from './utils';

/**
 * @module selectorator
 */

/**
 * @function createSelector
 *
 * @description
 * create a selector without any boilerplate code
 *
 * @example
 * import createSelector from 'selectorator';
 *
 * const getFilteredItems = createSelector(['items', 'filter.value'], (items, filterValue) => {
 *   return items.filter((item) => {
 *     return item.indexOf(filterValue) !== -1;
 *   });
 * });
 *
 * const state = {
 *   items: ['foo', 'bar', 'foo-bar'],
 *   filter: {
 *     value: 'foo'
 *   }
 * };
 *
 * console.log(getFilteredItems(state)); // ['foo', 'foo-bar'];
 * console.log(getFilteredItems(state)); // ['foo', 'foo-bar'], pulled from cache;
 *
 * @param paths paths to retrieve from state as parameters in getComputedValue, or
 * an object of key => path pairs that will assign path at state to key in structured selector
 * @param getComputedValue function that will accept the values at paths in state as parameters
 * and compute the next result
 * @param options additional options available for selector creation
 * @returns selector for state object passed
 */

function createSelector<State extends never, Output extends never>( // overload to signify errors
  paths: never[], // when path is empty
): never;

function createSelector<State, Output = any>( // overload for getIdentity
  paths: selectorator.PathWithoutObject[],
): selectorator.Selector<State, Output>;

function createSelector<State extends any[], Output = any>( // overload for getIdentity - multiParam
  paths: selectorator.Path[],
): selectorator.SelectorMultiParam<State, Output>;

function createSelector<State, Output extends PlainObject = PlainObject>( // overload for structured
  paths: Output,                                                        // selectors
): selectorator.Selector<
  State,
  {
    [key in keyof Output]: Output[key] extends ((
      ...args: any[]
    ) => infer Return)
      ? Return
      : any
  }
>;

function createSelector<State, Output>( // overload for standard selector
  paths: selectorator.PathWithoutObject[],
  getComputedValue: (...args: any) => Output,
  options?: selectorator.Options,
): selectorator.Selector<State, Output>;

function createSelector<State extends any[], Output>( // overload for selectors with path objects
  paths: selectorator.Path[],                        // for multiple parameters.
  getComputedValue: (...args: any) => Output,
  options?: selectorator.Options,
): selectorator.SelectorMultiParam<State, Output>;

function createSelector<State, Output>( // actual implementation - no changes
  paths: selectorator.Path[] | PlainObject,
  getComputedValue: (...args: any) => Output = identity,
  options: selectorator.Options = {},
): selectorator.Selector<State, Output> {
  const selectorCreator: Function = getSelectorCreator(options);

  if (Array.isArray(paths)) {
    if (!paths.length) {
      throw new ReferenceError(INVALID_ARRAY_PATHS_MESSAGE);
    }

    return <any>getStandardSelector(paths, selectorCreator, getComputedValue);
  }
  // added null check
  if (paths && paths !== null && typeof paths === 'object') {
    return <any>getStructuredSelector(paths, selectorCreator);
  }

  throw new TypeError(INVALID_PATHS_MESSAGE);
}

export default createSelector;
